// GeneralSection.js
/* GeneralSection component contains general info entry for the CV */

import React, {useState} from 'react'

class GeneralSection extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            firstName: '',
            lastName: '',
            email: '',
            phoneNumber: '',
            description: ''
        };
    }

    handleChange = (e) => {
      this.setState({
        firstName: e.target.value,
        lastName: e.target.value,
        email: e.target.value
      });
    };

    render () {
      return (
        <div className="GeneralInfo">
          <form>
            <input type="text">First Name:</input>
            <input type="text">Last Name:</input>
            <input type="email">email:</input>
            <input type="number">Phone Number:</input>
            <input type="text">description:</input>
            <button type="submit">Submit Info</button>
          </form>
        </div>
      )
    }
}

export default GeneralSection;
